declare namespace LeetCode {
  //Definition for a binary tree node.
  class TreeNode {
    val: number;
    left: TreeNode | null;
    right: TreeNode | null;
  }

  //Definition for singly-linked list.
  class ListNode {
    val: number;
    next: ListNode | null;
  }

  // Definition for node.
  class Node {
    val: number;
    children: Node[];
  }
}
